#ifndef __FvGlobalMacros_H__
#define __FvGlobalMacros_H__

#include <FvPowerDefines.h>

#undef min
#define min min
#undef max
#define max max

template <class T>
inline const T & min( const T & a, const T & b )
{
	return b < a ? b : a;
}

template <class T>
inline const T & max( const T & a, const T & b )
{
	return a < b ? b : a;
}



#endif /* __FvGlobalMacros_H__ */
